def run_tests(seq: np.ndarray):
    eligible_battery: dict = nistrng.check_eligibility_all_battery(
        seq, nistrng.SP800_22R1A_BATTERY)

    for name in eligible_battery.keys():
        print("-" + name)

    results = nistrng.run_all_battery(seq, eligible_battery, False)
    # Print results one by one
    print("Test results:")
    for result, elapsed_time in results:
        if result.passed:
            print("- PASSED - score: " + str(np.round(result.score, 3)) +
                  " - " + result.name + " - elapsed time: " + str(elapsed_time) + " ms")
        else:
            print("- FAILED - score: " + str(np.round(result.score, 3)) +
                  " - " + result.name + " - elapsed time: " + str(elapsed_time) + " ms")


run_tests(xp)
